/**
 * Push transport listener
 */
interface PushTransportListener {
    /**
     * Callback triggered upon connection
     */
    onConnection(): void;
    /**
     * Callback triggered upon disconnection
     * @param reasonId         Reason identifier
     * @param reasonParameters Reason parameters
     */
    onDisconnection(reasonId: string, reasonParameters: Map<string, string>): void;
    /**
     * Callback triggered upon message reception
     * @param mediaType Media type
     * @param content   Content
     */
    onMessage<T>(mediaType: string, content: T): void;
}
export { PushTransportListener };
