import { logger } from "./logger";

const signals = ["SIGINT"];

export const watchProcess = () => {
	// Exit nice on signal
	for (const signal of signals) {
		process.on(signal as any, () => {
			logger.warn(`Signal ${signal} received, exiting...`);
			process.exit(1);
		});
	}
	process.on("exit", () => {
		logger.debug("Goodbye !");
	});
};
