import { SeedLodash } from "./seedLodash";

/**
 * Shuffles an array
 * @param values T[]
 * @return a new array shuffled (does not touch the original array)
 */
export function shuffle<T>(values: T[]): T[] {
	return SeedLodash.instance.shuffle(values);
}

/**
 * Shuffles an array in place
 * @param values T[]
 * @return the array passed in params which has been shuffled
 */
export function shuffleInPlace<T>(values: T[]): T[] {
	let i = 0;
	let j = 0;
	let temp = null;

	for (i = values.length - 1; i > 0; i -= 1) {
		j = Math.floor(Math.random() * (i + 1));
		temp = values[i];
		values[i] = values[j];
		values[j] = temp;
	}
	return values;
}
