import seedrandom from "seedrandom";
import * as _ from "lodash";

export class SeedLodash {
	private static _instance: _.LoDashStatic = _.runInContext();

	public static get instance(): _.LoDashStatic {
		return SeedLodash._instance;
	}

	/**
	 * Seed the random and return the lodash instance.
	 * @param seed
	 */
	public static seed(seed: string): _.LoDashStatic {
		seedrandom(seed, { global: true });
		SeedLodash._instance = _.runInContext();

		return SeedLodash._instance;
	}
}
