#!/bin/bash

# Minimize all .js files in dist directory.
for i in dist/*.js; do
	terser --compress --mangle --comments /^!/ --output "${i%.js}.min.js" -- $i
done

# Replace gamekit.fdj|ds.js import by gamekit.fdj|ds.min.js in gamekit.bundle.min.js
sed -i -e 's/\("gamekit\."\S*\)"\.js"/\1".min.js"/' dist/gamekit.bundle.min.js
