export const TEMPLATE_SQL_USR = (
	description: string,
	bic: string,
	bicVariant: string,
	exsCode: string,
	ref: string
) =>
	`UPDATE REF_USR_EXTERNAL_SERVICE SET
EXS_NAME = '${ref}',
EXS_DESCRIPTION = '${description}',
EXS_TYPE = 'Multijoueur',
EXS_ACCOUNT_NUMBER = '${bic}',
EXS_VARIANT_CODE = '${bicVariant}',
EXS_CREATED = SYSDATE,
EXS_UPDATED = SYSDATE
WHERE ECO_CODE ='MTP' AND EXS_CODE = '${exsCode}';

INSERT INTO REF_USR_EXTERNAL_SERVICE (ECO_CODE,EXS_CODE, EXS_NAME, EXS_DESCRIPTION, EXS_TYPE, EXS_ACCOUNT_NUMBER, EXS_VARIANT_CODE, EXS_CREATED, EXS_UPDATED)
VALUES ('MTP', '${exsCode}', '${ref}', '${description}', 'Multijoueur', '${bic}', '${bicVariant}', SYSDATE, SYSDATE);

INSERT INTO USR_SITE_OFFER (ECO_CODE, EXS_CODE, SIT_CODE, SIO_STATUS, SIO_CREATED, SIO_UPDATED)
VALUES ('MTP', '${exsCode}', 'FDJ_WEB', 'C', SYSDATE, SYSDATE);

COMMIT;`;
